{{ ((chartType) => ( 
(() => {
  const data = getFaturamentoPV.data.filter(r => r.ponto_venda === selPV.selectedOptionValue);
  const meses = [...new Set(data.map(r => r.mes_nome))];
  const anoAtual = Number(selAno.selectedOptionValue);
  const anoAnterior = anoAtual - 1;

  const valoresAtual = meses.map(m =>
    (data.find(r => r.mes_nome === m && r.ano === anoAtual)?.valor_faturado) || 0
  );
  const valoresAnterior = meses.map(m =>
    (data.find(r => r.mes_nome === m && r.ano === anoAnterior)?.valor_faturado) || 0
  );

  return {
    title: {
      text: `Faturamento Mensal — ${selPV.selectedOptionLabel || ''} (${anoAtual})`,
      left: 'center',
      textStyle: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#333'
      }
    },
    tooltip: { trigger: 'axis' },
    legend: {
      data: ['Ano Atual', 'Ano Anterior'],
      top: 30
    },
    xAxis: { type: 'category', data: meses },
    yAxis: {
      type: 'value',
      axisLabel: {
        formatter: function (v) {
          return 'R$ ' + Number(v).toLocaleString('pt-BR');
        }
      }
    },
    series: [
      { name: 'Ano Atual', type: 'bar', data: valoresAtual },
      { name: 'Ano Anterior', type: 'line', data: valoresAnterior }
    ],
    color: ['#553DE9', '#AAAAAA']
  };
})()
))(Chart1.chartType) }}
