onClick:
{{
  (() => {
    const dados = insertVendedor.data?.[0];
    const urlAcesso = "https://vendas360.duasdesign.com"; // mesmo link

    if (!dados) {
      showAlert("Não foi possível montar a mensagem.", "error");
      return;
    }

    const msg = 
`Olá ${dados.nome},

Seu acesso ao sistema Vendas 360 foi criado. Seguem os seus dados:

Usuário: ${dados.nome_usuario}
Senha temporária: 1234
Link de acesso: ${urlAcesso}

No primeiro acesso, o sistema vai pedir para você alterar essa senha temporária por uma senha definitiva.`;

    copyToClipboard(msg);
    showAlert("Mensagem copiada! É só colar no WhatsApp do vendedor.", "success");
  })()
}}
