text:
{{
  (() => {
    const atual = Number(Query_VendasMes_Gestao.data[0]?.total_vendido || 0);
    const anterior = Number(FatPeriodoAnt.data[0]?.total_vendido_anterior || 0);
    if (anterior === 0) return "—";
    const variacaoPerc = ((atual - anterior) / anterior) * 100;
    const percentual = (variacaoPerc > 0 ? "+" : "") +
      variacaoPerc.toLocaleString("pt-BR", { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + "%";
    // COR DINÂMICA: verde se positivo ou zero, vermelho se negativo
    const cor = variacaoPerc >= 0 ? "#28a745" : "#ff0000";
    return `<div style='display:flex;flex-direction:column;align-items:center;width:100%;'>
        <span style='font-size:18px;font-weight:bold;color:#fff;'>R$ ${anterior.toLocaleString("pt-BR", { minimumFractionDigits: 2, maximumFractionDigits: 2 })} <span style='font-size:16px;font-weight:bold;color:${cor};'>(${percentual})</span></span><span style='font-size:12px;color:#fff;'>Mesmo período do mês anterior</span>
    </div>`;
  })()
}}
