text:
{{
  (() => {
    const atual = Number(Query_QtdVendasMes_Gestao.data[0]?.qtd_vendas || 0);
    const anterior = Number(QtdVendasPeriodoAnt.data[0]?.qtd_vendas_anterior || 0);
    if (anterior === 0) return "—";
    const variacaoPerc = ((atual - anterior) / anterior) * 100;
    const percentual = (variacaoPerc > 0 ? "+" : "") + variacaoPerc.toLocaleString("pt-BR", { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + "%";
    return `<div style='display:flex;flex-direction:column;align-items:center;width:100%;'>
        <span style='font-size:14px;font-weight:bold;color:#fff;'>${anterior} <span style='font-size:16px;font-weight:normal;color:#fff;'>(${percentual})</span></span>
        <span style='font-size:12px;color:#fff;'>Mesmo período do mês anterior</span>
    </div>`;
  })()
}}
