{{
  VendasMes_PDV.data
    .filter(d => d.total_vendido > 0)
    .map(d => {
      const loja = d.ponto_venda?.trim();
      const valor = d.total_vendido;
      const metaObj = MetasMes_PDV.data.find(m => m.ponto_venda?.trim() === loja);
      const meta = metaObj?.vlr_meta || 0;

      return {
        x: loja,
        y: Number(valor.toFixed(2)),  // garante 2 casas decimais para o grÃ¡fico
        color: valor >= meta ? "#28a745" : "#e74c3c",
        label: valor.toLocaleString("pt-BR", { minimumFractionDigits: 2, maximumFractionDigits: 2 })
      };
    })
}}

customEChartConfig:
{{ ((chartType) => ( 
(() => {
  const vendas = Object.fromEntries(
    VendasMes_PDV.data.map(d => [d.ponto_venda?.trim(), d.total_vendido || 0])
  );

  const metas = Object.fromEntries(
    MetasMes_PDV.data.map(d => [d.ponto_venda?.trim(), d.vlr_meta || 0])
  );

  const pdvs = Object.keys(vendas).filter(loja => vendas[loja] > 0);

  const seriesData = pdvs.map(loja => ({
    name: loja,
    value: Number(vendas[loja].toFixed(2)),
    itemStyle: {
      color: vendas[loja] >= (metas[loja] || 0) ? "#28a745" : "#e74c3c"
    },
    label: {
      show: true,
      position: 'top',
      formatter: params =>
        params.value.toLocaleString("pt-BR", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2
        })
    }
  }));

  const markLines = pdvs.map(loja => ({
    xAxis: loja,
    yAxis: metas[loja] || 0
  }));

  return {
    tooltip: {
      trigger: 'item',
      formatter: function (params) {
        const loja = params.name;
        const valor = params.value.toLocaleString("pt-BR", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2
        });
        const meta = (metas[loja] || 0).toLocaleString("pt-BR", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2
        });
        return `${loja}<br/>Faturamento: R$ ${valor}<br/>Meta: R$ ${meta}`;
      }
    },
    grid: { left: '5%', right: '5%', bottom: '10%', containLabel: true },
    xAxis: {
      type: 'category',
      data: pdvs
    },
    yAxis: {
      type: 'value',
      axisLabel: {
        show: false // <--- remove os valores do eixo Y
      }
    },
    series: [
      {
        type: 'bar',
        name: 'Faturamento',
        data: seriesData,
        markLine: {
          silent: true,
          data: markLines,
          label: {
            show: true,
            position: 'start',
            formatter: function(params) {
              const metaVal = params.data.yAxis || 0;
              return metaVal.toLocaleString("pt-BR", {
                style: 'currency',
                currency: 'BRL',
                minimumFractionDigits: 2
              });
            },
            color: '#007bff',
            fontSize: 10
          },
          lineStyle: {
            type: 'dashed',
            color: '#007bff',
            width: 1
          }
        }
      }
    ]
  };
})()
))(Chart_Vendas_PDVCopy.chartType) }}

customFusionChartConfig:
{{
{
  type: "bar2d",
  dataSource: {
    chart: {
      caption: "Faturamento x Meta por PDV",
      xAxisName: "Faturamento (R$)",
      yAxisName: "PDVs",
      theme: "fusion",
      decimals: "2",
      formatNumberScale: "0",
      showValues: "1",
      usePlotGradientColor: "0",
      plotFillAlpha: "80",
      baseFont: "Inter",
      baseFontSize: "12"
    },
    data: Query_TodosPDVs.data.map(pdv => {
      const venda = Query_VendasMes_Gestao.data.find(item => item.ponto_venda === pdv.ponto_venda);
      const meta = Query_MetaMes_Gestao.data.find(item => item.ponto_venda === pdv.ponto_venda);

      const faturamento = venda?.total_vendido || 0;
      const valor_meta = meta?.vlr_meta || 0;

      return {
        label: pdv.ponto_venda,
        value: faturamento,
        color: faturamento >= valor_meta ? "#28a745" : "#e74c3c",
        toolText: 
          "Faturamento: " + faturamento.toLocaleString("pt-BR", { style: "currency", currency: "BRL" }) +
          "\nMeta: " + valor_meta.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })
      };
    }),
    trendlines: [
      {
        line: Query_TodosPDVs.data.map(pdv => {
          const meta = Query_MetaMes_Gestao.data.find(item => item.ponto_venda === pdv.ponto_venda);
          return {
            startvalue: meta?.vlr_meta || 0,
            color: "#007bff",
            displayvalue: "Meta",
            thickness: "1",
            valueOnRight: "1",
            dashed: "1"
          };
        })
      }
    ]
  }
}
}}
