text:
{{
  (() => {
    const dataSelecionada = moment(DatePicker_ResumoDia.selectedDate);
    const hoje = moment();
    const ehHoje = dataSelecionada.isSame(hoje, "day");

    return !ehHoje
      ? `<div style="background:#FFF3CD; color:#856404; border:1px solid #FFEEBA; border-radius:4px; padding:8px; font-size:13px; text-align:center;"><span style="font-weight:bold;">ℹ️</span> Você está visualizando dados do dia <b>${dataSelecionada.format("DD/MM/YYYY")}</b>.<br>
          Para retornar ao dia atual, selecione a data de hoje no calendário.</div>`
      : "";
  })()
}}
