{{
  (() => {
    const progresso = Query_VendasMes.data[0]?.total_vendido / Query_MetaVendedorMes.data[0]?.meta_valor;

    if (progresso >= 1.2) return "🚀 Você tá voando!";

    if (progresso >= 1.1) {
      const frases = [
        "🚀 Você tá voando!",
        "💥 Passou da meta com estilo!",
        "✨ Bateu e seguiu! Isso inspira.",
        "🏅 Entrega acima da meta! Isso é consistência."
      ];
      return frases[Math.floor(Math.random() * frases.length)];
    }

    if (progresso >= 1.0) return "🎯 Meta atingida! Excelente trabalho!";
    if (progresso >= 0.95) return "💪 Tá quase! Só mais um empurrão!";
    if (progresso >= 0.90) return "⚡ Força total na reta final!";

    return "🔥 A meta te espera! Bora bater!";
  })()
}}
